import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    1: {
        id: 1,
        completed: true,
        text: 'complete online javascript course',
    },
    2: {
        id: 2,
        completed: false,
        text: 'jog around the park 3x',
    },
    3: {
        id: 3,
        completed: false,
        text: '10 minutes meditation',
    },
    4: {
        id: 4,
        completed: false,
        text: 'read for 1 hour',
    },
}

const todosSlice = createSlice({
    name: 'todos',
    initialState,
    reducers: {
        todoAdded(state, action) {
            const todo = action.payload
            state[todo.id] = todo
        },
        todoDeleted(state, action) {
            const id = action.payload
            delete state[id]
        },
        todoToggled(state, action) {
            const id = action.payload
            state[id].completed = !state[id].completed
        },
        completedTodosDeleted(state, action) {
            Object.values(state).forEach(todo => {
                if(todo.completed) {
                    delete state[todo.id]
                }
            })
        }
    }
})

export const {todoAdded, todoDeleted, todoToggled, completedTodosDeleted} = todosSlice.actions

export default todosSlice.reducer
