// styles
import './Todo.scss'

// icons 
import CloseIcon from '@mui/icons-material/Close'
import CheckCircleIcon from '@mui/icons-material/CheckCircle'

import { useDispatch } from 'react-redux'
import { todoDeleted, todoToggled } from './store/todosSlice'

function Todo(props) {

    const dispatch = useDispatch()

    // delete todo 
    const deleteTodo = () => {
        dispatch(todoDeleted(id))
    }

    // toggle completed status of todo
    const toggleTodo = () => {
        dispatch(todoToggled(id))
    }

    const {id, completed, text} = props.todo

    return (
        <div className='todo'>
            
            <input
                className='todo__checkbox'
                type='checkbox'
                checked={completed}
                onChange={toggleTodo}
            />

            <svg width={0} height={0}>
                <linearGradient id="myGradient" gradientTransform="rotate(90)">
                    <stop offset="25%"  stop-color="hsl(192, 100%, 67%)" />
                    <stop offset="100%" stop-color="hsl(280, 87%, 65%)" />
                </linearGradient>
            </svg>

            <CheckCircleIcon
                className='todo__icon'
                sx={{fill: "url(#myGradient)"}}
            />

            <span className={`todo__text ${completed && 'completed'}`}>
                {text}
            </span>

            <CloseIcon
                className='todo__closeBtn'
                onClick={deleteTodo}
            />
    
        </div>
    )
}

export default Todo
