// styles
import './Filters.scss'

import { useState } from 'react'
import { useDispatch } from 'react-redux'

function Filters() {

    const dispatch = useDispatch()

    const [curr, setCurr] = useState('all')

    // change current selected status
    const changeFilter = (e) => {
        setCurr(e.target.getAttribute('data-val'))
    }

    return (
        <div className='filters'>
    
            <span 
                className={`filter ${curr=='all' && 'active'}`}
                data-val='all'
                onClick={changeFilter}
            >
                all
            </span>

            <span 
                className={`filter ${curr=='active' && 'active'}`}
                data-val='active'
                onClick={changeFilter}
            >
                active
            </span>

            <span 
                className={`filter ${curr=='completed' && 'active'}`}
                data-val='completed'
                onClick={changeFilter}
            >
                completed
            </span>
        
        </div>
    )
}

export default Filters
