import { configureStore } from '@reduxjs/toolkit'

import todosReducer from './todosSlice.js'
import FiltersReducer from './FiltersSlice.js'

const store = configureStore({
    reducer: {
        todos: todosReducer,
        filters: filterReducer,
    }
})

export default store
